import User from "../Components/user"
import axios from 'axios'
import { useEffect, useState } from "react"
import Category from "../Components/category"

function Categorylist() {

    const [categories, setCategory] = useState([])
    const [newcategory,setNewcategory] = useState('')

    const getcategories = async () => {

        const response = await axios.get('http://localhost:4000/category')
        const result = response.data
        if (result.status == 'success') {
            setCategory(result.result)
        }

    }

    const addCategory = async()=>{

        const body = {
            newcategory,
        }
        const response = await axios.post('http://localhost:4000/category/addcategory', body)
        const result = response.data

    }

    return (
        <div className="container">

            <label htmlFor="">Cetegory</label>
            <input type="text" className="form-control" onChange={(e)=>{
                setNewcategory(e.target.value)
            }} />
            <button className="btn btn-secondary" onClick={addCategory}>Add Category</button>


            <table className="table table-striped">
                <thead>
                    <th>Id</th>
                    <th>Category</th>
                    <th>Action</th>
                </thead>
                <tbody>
                    {
                        categories.map((category) => {
                            return <Category props={category}></Category>
                        })
                    }
                </tbody>
            </table>

        </div>
    )


}

export default Categorylist
