import useState from "../page/userList"
import axios from "axios"
import { useNavigate } from "react-router-dom"

 function Changeemail(){
const navigate = useNavigate()


const [oldemail,setOldemail] = useState('')
const [newemail,setNewemail] = useState('')

const setEmail = async()=>{
    const body = {
        oldemail,newemail
    }
    const response = await axios.put('http://localhost:4000/user/profile/editEmail',body)
    const result = response.data
    if(result.status == 'success'){
        navigate('/profile/edit')
    }
    else{
        alert('failed')
    }

}



return(
    <div className="container">

<div>
    <label htmlFor="" >Old Email</label>
    <input type="email" className="form-control" onChange={(e)=>{
        setOldemail(e.target.value)
    }} />
</div>
<div>
    <label htmlFor="" >New Email</label>
    <input type="email" className="form-control" onChange={(e)=>{
        setNewemail(e.target.value)
    }}   />
</div>
<div>
    <button className="btn btn-success" onClick={setEmail}> Set Email</button>
</div>

    </div>
)
}
export default Changeemail
