import axios from "axios"
import { useEffect } from "react"

function Blog({props}){

    const {id, title,ctitle,createdtime,uname} = props
    const editbtn = async()=>{
        const response = await axios.get("https://localhost:4000/blog/editbtn")
        const result = response.data
        if(result.status === 'success'){

            if(result.result[0].name === 'uname'){
                return(
                    <tr align='center'>
                        <td>{id}</td>
                        <td>{title}</td>
                        <td>{ctitle}</td>
                        <td>{createdtime}</td>
                        <td>{uname}</td>
                        <td className=""><button className="btn btn-warning">edit</button><button className="btn btn-danger">delete </button></td>

                    </tr>
                )
            }else{
                return(
                    <tr align='center'>
                        <td>{id}</td>
                        <td>{title}</td>
                        <td>{ctitle}</td>
                        <td>{createdtime}</td>
                        <td>{uname}</td>

                    </tr>
                )
            }

        }else{

        }

    }

    useEffect(()=>{
        editbtn()
    },[])
return(
    <tr align='center'>
        <td>{id}</td>
        <td>{title}</td>
        <td>{ctitle}</td>
        <td>{createdtime}</td>
        <td>{uname}</td>
        <td className=""><button className="btn btn-warning">edit</button><button className="btn btn-danger">delete </button></td>

    </tr>
)
}

export default Blog
